 class=&#34;line&#34;&gt;q &lt;span class=&#34;string&#34;&gt;&amp;quot;N&amp;quot;&lt;/span&gt;  &lt;span class=&#34;string&#34;&gt;&amp;quot;P&amp;quot;&lt;/span&gt;  &lt;span class=&#34;string&#34;&gt;&amp;quot;Q&amp;quot;&lt;/span&gt; &lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
